package dailyclass;

public class datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/**
		 * 1 . Variables --> It is a name which is giving to the memory location, Which
		 * is used to store the values . 
		 * 2 . Data Type --> Data type will define the
		 * type of values should be stored in a Variable .
		 * 
		 * 1 . Does JAVA is OOPS ? Yes 2 . Does JAVA is 100 % OOPS ? No (Java Support
		 * premitive Data Type) .
		 * 
		 * Types Of DataType ================= 1 . Premitive Type (It is an inbuild
		 * keywords). - byte (1 Byte) - short(2 Byte) - int (4 Byte) - long (8 Byte) -
		 * float (4 Byte) 0.0f - double(8 Byte) 0.0d - char (2 Byte) \u0000 - boolean (1
		 * Bit) false
		 * 
		 * 2 . Non-Premitive Type (It is an inbuild class). - String (Null) - Integer -
		 * Long - Float - Double - Boolean - Array
		 */
		
		byte age = 127; // -128 to +127
		System.out.println("Age is : "+age);
		short s = 4555;
		System.out.println("Short value is : "+s);
		int i = 21215454;
		System.out.println("Int Value is : "+i);
		long adhar = 2132165465454l;
		System.out.println("Adhar value is : "+adhar);
		
		float f = 34344.5543f;
		System.out.println("Floating value is : "+f);
		
		double d = 34254352.5543253254324;
		System.out.println("Double value is : "+d);
		
		char c = 'T';
		System.out.println("Character is : "+c);
		
		boolean b = true;
		System.out.println("Boolean value is : "+b);
		
		String name = "Credo System";
		System.out.println("Institute Name is : "+name);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
